!function(){var e=function(){for(var e=$("#day").val(),t=$("#month").val()-1,a=$("#year").val(),n=new Date(a,t,e),o=n.getTime(),p=new Date,r=p.getTime(),i=864e5,l=(r-o)/i,v=1,h=0;l>Math.pow(10,h);)h+=1,v=10*v;var m=i*Math.pow(10,h)+o,u=new Date(m);$("#math-birthday").empty().append(v),$("#next-date").empty().append(u.getMonth()+1+"/"+u.getDate()+"/"+u.getFullYear()+"!")};$("button").on("click",function(t){t.preventDefault(),e()})}();