var calculate=function(){for(var e=$("#day").val(),t=$("#month").val()-1,a=$("#year").val(),n=new Date(a,t,e),l=n.getTime(),o=new Date,p=o.getTime(),c=864e5,r=(p-l)/c,u=p-l,v=1,h=0;r>Math.pow(10,h);)h+=1,v=10*v;var i=c*Math.pow(10,h)+l,m=i/c,d=new Date(i);$("#math-birthday").empty().append(v),$("#next-date").empty().append(d.getMonth()+1+"/"+d.getDate()+"/"+d.getFullYear()+"!")};$("button").on("click",function(e){e.preventDefault(),calculate()});